function getFraction(a){a=100*a;var b=simplifyFraction(a,100),c=b[0]+"/"+b[1];return c}function simplifyFraction(a,b){return greatestCommonFactor=calculateGreatestCommonFactor(a,b),[a/greatestCommonFactor,b/greatestCommonFactor]}function calculateGreatestCommonFactor(a,b){if(b){var c=parseInt(b);return 1===c||0===c?1:calculateGreatestCommonFactor(b,a%b)}return a}$.fn.fractionify=function(){return 0===this.length&&console.log("none found"),this.each(function(){this.value=getFraction(this.value)})};