$.fn.fractionify=function(e){e=e||.0625;return this.each(function(){this.setAttribute("step",e);this.value=getFractionValue(this.value)})};getFractionValue=function(e){e=e*100;var t=simplifyFraction(e,100);var n=t[0]+"/"+t[1];return n};simplifyFraction=function(e,t){var n=function r(e,t){return t?r(t,e%t):e};commondenom=n(e,t);return[e/commondenom,t/commondenom]}